svg.script:
var d=new Date();
var n=d.getHours();
var m=d.getMinutes();
var sec=d.getSeconds();
var hora=360/12;
var graus=(n-12)*hora;
var partedehora=m/60;
var horgraus=parseInt(graus+(partedehora*hora));
document.getElementById("ghou").setAttribute("transform","translate(100,100) scale(2,2) rotate("+horgraus+",100,100)");
var minuto=360/60;
var mingraus=m*minuto;
var decimin=parseInt((sec/60)*10)/10;
mingraus=parseInt((m+decimin)*minuto);
//alert(minuto + " " + decimin + " " +mingraus);
document.getElementById("gmin").setAttribute("transform","translate(100,100) scale(2,2) rotate("+mingraus+",100,100)");

var svgTextElement = document.getElementById("horamin");
var textNode = svgTextElement.childNodes[0];

var z=0;
var newpath;

var delta=45;
for(z=1;13>z;z++){
	svgz=document.getElementById('gg');
	delta=delta+30;
	newpath=document.createElementNS(document.rootElement.namespaceURI,"circle");
	newpath.setAttribute("transform","rotate("+delta+",100,100)");
	newpath.setAttributeNS(null,"cx","0");
	newpath.setAttributeNS(null,"cy","0");
	newpath.setAttributeNS(null,"r","5");
	svgz.appendChild(newpath);
}

var delta=45;
for(z=1;61>z;z++){
	svgz=document.getElementById('t');
	delta=delta+6;
	newpath=document.createElementNS(document.rootElement.namespaceURI,"circle");
	newpath.setAttribute("transform","translate(160,160) scale(1.4,1.4) rotate("+delta+",100,100)");
	newpath.setAttributeNS(null,"cx","0");
	newpath.setAttributeNS(null,"cy","0");
	newpath.setAttributeNS(null,"r","1");
	newpath.setAttributeNS(null,"stroke","#060");
	newpath.setAttributeNS(null,"stroke-width","2");
	newpath.setAttributeNS(null,"fill","none");
	svgz.appendChild(newpath);
}

function retime(){
	d=new Date();
	n=d.getHours();
	m=d.getMinutes();
	sec=d.getSeconds();
	hora=360/12;
	graus=(n-12)*hora;
	partedehora=m/60;
	horgraus=parseInt(graus+(partedehora*hora));
	document.getElementById("ghou").setAttribute("transform","translate(100,100) scale(2,2) rotate("+horgraus+",100,100)");
	minuto=360/60;
	decimin=parseInt((sec/60)*10)/10;
	mingraus=parseInt((m+decimin)*minuto);
	document.getElementById("gmin").setAttribute("transform","translate(100,100) scale(2,2) rotate("+mingraus+",100,100)");
var strm="";
if(10>m){strm="0";}
	textNode.nodeValue=n+":"+strm+m;
}

var myvar=setInterval(atutime,10000);

function atutime(){retime();}


var w=window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth;
var h=window.innerHeight || document.documentElement.clientHeight || document.body.clientHeight;
//alert(w+" "+h);
svgz=document.getElementById('t');
svgz.setAttributeNS(null,"width",w);
svgz.setAttributeNS(null,"height",h);
var reco=document.getElementById('rectt');
reco.setAttributeNS(null,"width",w);
reco.setAttributeNS(null,"height",h);
